}

.dark {
  --background: hsl(222, 84%, 4.9%);
  --foreground: hsl(210, 40%, 98%);
  --muted: hsl(217, 32%, 17%);
  --muted-foreground: hsl(215, 20%, 65%);
  --popover: hsl(222, 84%, 4.9%);
  --popover-foreground: hsl(210, 40%, 98%);
  --card: hsl(222, 84%, 4.9%);
  --card-foreground: hsl(210, 40%, 98%);
  --border: hsl(217, 32%, 17%);
  --input: hsl(217, 32%, 17%);
  --primary: hsl(262, 83%, 58%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(217, 32%, 17%);
  --secondary-foreground: hsl(210, 40%, 98%);
  --accent: hsl(193, 95%, 68%);
  --accent-foreground: hsl(222, 84%, 4.9%);
  --destructive: hsl(0, 62%, 30%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --ring: hsl(262, 83%, 58%);
  --radius: 0.5rem;
  --success: hsl(142, 76%, 36%);
  --warning: hsl(43, 96%, 56%);
  --error: hsl(0, 62%, 30%);
  --neutral: hsl(217, 32%, 17%);
  --therapeutic: hsl(189, 85%, 25%);
  --calming: hsl(260, 60%, 15%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased text-foreground;
    background: var(--background);
    min-height: 100vh;
    height: 100vh;
  }

  html {
    height: 100%;
  }
}

@layer utilities {
  .bg-gradient-primary {
    background: linear-gradient(135deg, hsl(262, 83%, 58%) 0%, hsl(193, 95%, 68%) 100%);
  }

  .bg-gradient-neutral {
    background: linear-gradient(135deg, var(--therapeutic) 0%, var(--calming) 100%);
  }

  .dark .bg-gradient-neutral {
    background: linear-gradient(135deg, hsl(222, 84%, 4.9%) 0%, hsl(217, 32%, 17%) 100%);
  }

  .bg-gradient-therapeutic {
    background: linear-gradient(135deg, hsl(189, 85%, 85%) 0%, hsl(260, 60%, 92%) 35%, hsl(193, 95%, 88%) 100%);
  }

  .dark .bg-gradient-therapeutic {
    background: linear-gradient(135deg, hsl(189, 85%, 15%) 0%, hsl(260, 60%, 8%) 35%, hsl(193, 95%, 12%) 100%);
  }

  .animate-pulse-soft {
    animation: pulse-soft 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  @keyframes pulse-soft {
    0%, 100% {
      opacity: 1;
    }
    50% {
      opacity: 0.7;
    }
  }

  /* Custom scrollbar styles */
  .scrollbar-thin {
    scrollbar-width: thin;
    scrollbar-color: hsl(var(--border)) transparent;
  }

  .scrollbar-thin::-webkit-scrollbar {
    width: 6px;
    height: 6px;
  }

  .scrollbar-thin::-webkit-scrollbar-track {
    background: transparent;
  }

  .scrollbar-thin::-webkit-scrollbar-thumb {
    background-color: hsl(var(--border));
    border-radius: 3px;
  }

  .scrollbar-thin::-webkit-scrollbar-thumb:hover {
    background-color: hsl(var(--muted-foreground));
  }

  .scrollbar-thumb-border {
    scrollbar-color: hsl(var(--border)) transparent;
  }

  .scrollbar-track-background {
    scrollbar-color: hsl(var(--border)) hsl(var(--background));
  }
}

