import { useState, useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import { Send, User, UserCheck } from "lucide-react";
import type { Message } from "@shared/schema";

interface ChatInterfaceProps {
  therapistName: string;
  onSendMessage: (content: string, isVoice: boolean) => void;
  messages: Message[];
  isLoading: boolean;
}

export default function ChatInterface({ 
  therapistName, 
  onSendMessage, 
  messages, 
  isLoading 
}: ChatInterfaceProps) {
  const [inputMessage, setInputMessage] = useState("");
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Focus input on mount
  useEffect(() => {
    inputRef.current?.focus();
  }, []);

  const handleSendMessage = () => {
    if (inputMessage.trim() && !isLoading) {
      onSendMessage(inputMessage.trim(), false);
      setInputMessage("");
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatTime = (timestamp: Date) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className="h-full flex flex-col">
      {/* Messages Area */}
      <div className="flex-1 overflow-y-auto space-y-4 min-h-0">
        {messages.length === 0 ? (
          <div className="text-center py-12">
            <div className="w-16 h-16 bg-primary/20 rounded-full flex items-center justify-center mx-auto mb-4">
              <UserCheck className="w-8 h-8 text-primary" />
            </div>
            <h3 className="text-lg font-semibold text-foreground mb-2">
              Welcome to your session with {therapistName}
            </h3>
            <p className="text-muted-foreground">
              Start by sharing what's on your mind today. I'm here to listen and support you.
            </p>
          </div>
        ) : (
          messages.map((message, index) => (
            <div
              key={message.id || index}
              className={`flex items-start space-x-3 ${
                message.isUser ? 'justify-end' : 'justify-start'
              }`}
            >
              {!message.isUser && (
                <div className="w-10 h-10 bg-primary rounded-full flex items-center justify-center flex-shrink-0">
                  <UserCheck className="w-5 h-5 text-white" />
                </div>
              )}

              <div className={`flex-1 max-w-xs md:max-w-md ${message.isUser ? 'order-1' : ''}`}>
                <Card className={`
                  ${message.isUser 
                    ? 'bg-primary text-primary-foreground' 
                    : 'bg-accent/10 border-accent/20'
                  }
                `}>
                  <CardContent className="p-4">
                    {!message.isUser && (
                      <p className="text-sm font-semibold text-accent mb-1">
                        {therapistName}
                      </p>
                    )}
                    <p className="text-sm leading-relaxed">
                      {message.content}
                    </p>
                  </CardContent>
                </Card>
                <p className={`text-xs text-muted-foreground mt-1 ${
                  message.isUser ? 'text-right' : 'text-left'
                }`}>
                  {formatTime(message.createdAt || new Date())}
                </p>
              </div>

              {message.isUser && (
                <div className="w-10 h-10 bg-muted rounded-full flex items-center justify-center flex-shrink-0">
                  <User className="w-5 h-5 text-muted-foreground" />
                </div>
              )}
            </div>
          ))
        )}

        {/* Loading indicator */}
        {isLoading && (
          <div className="flex items-start space-x-3 justify-start">
            <div className="w-10 h-10 bg-primary rounded-full flex items-center justify-center">
              <UserCheck className="w-5 h-5 text-white" />
            </div>
            <div className="flex-1">
              <Card className="bg-accent/10 border-accent/20">
                <CardContent className="p-4">
                  <p className="text-sm font-semibold text-accent mb-1">
                    {therapistName}
                  </p>
                  <div className="flex items-center space-x-2">
                    <div className="w-2 h-2 bg-accent rounded-full animate-pulse"></div>
                    <div className="w-2 h-2 bg-accent rounded-full animate-pulse" style={{animationDelay: '0.2s'}}></div>
                    <div className="w-2 h-2 bg-accent rounded-full animate-pulse" style={{animationDelay: '0.4s'}}></div>
                    <span className="text-sm text-muted-foreground ml-2">
                      typing...
                    </span>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <div className="border-t border-border p-4 bg-background dark:bg-background">
        <div className="flex items-center space-x-3">
          <div className="flex-1">
            <Input
              ref={inputRef}
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Type your message..."
              disabled={isLoading}
              className="pr-12 bg-background border-border"
            />
          </div>
          <Button
            onClick={handleSendMessage}
            disabled={!inputMessage.trim() || isLoading}
            className="bg-primary hover:bg-primary/90"
          >
            <Send className="w-4 h-4" />
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/components/crisis-modal.tsx.content:
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { AlertTriangle, Phone, ExternalLink } from "lucide-react";

interface CrisisModalProps {
  isOpen: boolean;
  onClose: () => void;
  detectedKeywords: string[];
}

export default function CrisisModal({ isOpen, onClose, detectedKeywords }: CrisisModalProps) {
  const handleCallCrisisLine = () => {
    window.open('tel:988', '_self');
  };

  const handleCallEmergency = () => {
    window.open('tel:911', '_self');
  };

  const handleFindResources = () => {
    window.open('https://suicidepreventionlifeline.org/', '_blank');
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-md mx-4">
        <DialogHeader>
          <div className="flex flex-col items-center text-center">
            <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mb-4">
              <AlertTriangle className="w-8 h-8 text-red-600" />
            </div>
            <DialogTitle className="text-xl font-bold text-foreground">
              We're Concerned About You
            </DialogTitle>
          </div>
        </DialogHeader>

        <div className="space-y-4">
          <p className="text-center text-muted-foreground">
            It sounds like you might be going through a really difficult time. Please know that help is available, and you don't have to face this alone.
          </p>

          {detectedKeywords.length > 0 && (
            <Card className="bg-red-50 border-red-200">
              <CardContent className="p-3">
                <p className="text-sm text-red-800 font-medium">
                  We detected some concerning language in your message. Your safety is our priority.
                </p>
              </CardContent>
            </Card>
          )}

          <div className="space-y-3">
            <Button
              onClick={handleCallCrisisLine}
              className="w-full bg-red-600 hover:bg-red-700 text-white"
            >
              <Phone className="w-4 h-4 mr-2" />
              Call 988 - Crisis Lifeline
            </Button>

            <Button
              onClick={handleCallEmergency}
              className="w-full bg-red-800 hover:bg-red-900 text-white"
            >
              <Phone className="w-4 h-4 mr-2" />
              Call 911 - Emergency
            </Button>

            <Button
              onClick={handleFindResources}
              variant="outline"
              className="w-full border-red-300 text-red-700 hover:bg-red-50"
            >
              <ExternalLink className="w-4 h-4 mr-2" />
              Find Local Resources
            </Button>
          </div>

          <div className="border-t pt-4">
            <div className="flex space-x-3">
              <Button
                variant="outline"
                onClick={onClose}
                className="flex-1"
              >
                Continue Session
              </Button>
              <Button
                onClick={handleFindResources}
                className="flex-1 bg-primary hover:bg-primary/90"
              >
                Get Help Now
              </Button>
            </div>
          </div>

          <div className="text-center">
            <p className="text-xs text-muted-foreground">
              If you're in immediate danger, please call 911 or go to your nearest emergency room.
            </p>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/session-summary-mo.content:
import { useQuery } from "@tanstack/react-query";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { 
  Clock, 
  Target, 
  Lightbulb, 
  CheckSquare, 
  Heart,
  Download,
  Mail,
  Check
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { Session } from "@shared/schema";

interface SessionSummaryModalProps {
  isOpen: boolean;
  onClose: () => void;
  sessionId: number | null;
  therapistName: string;
  duration: number;
  onViewAllSummaries?: () => void;
  onReturnHome?: () => void;
}

export default function SessionSummaryModal({ 
  isOpen, 
  onClose, 
  sessionId, 
  therapistName, 
  duration,
  onViewAllSummaries,
  onReturnHome 
}: SessionSummaryModalProps) {
  const { toast } = useToast();

  const { data: session, isLoading } = useQuery({
    queryKey: ["/api/sessions", sessionId],
    enabled: isOpen && !!sessionId,
  });

  const formatDuration = (seconds: number) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes} minutes${remainingSeconds > 0 ? ` ${remainingSeconds} seconds` : ''}`;
  };

  const handleDownloadPDF = () => {
    toast({
      title: "Feature Coming Soon",
      description: "PDF download functionality will be available in the next update.",
    });
  };

  const handleEmailSummary = () => {
    toast({
      title: "Feature Coming Soon", 
      description: "Email summary functionality will be available in the next update.",
    });
  };

  const handleFinish = () => {
    toast({
      title: "Session Complete",
      description: "Thank you for your session today. Take care of yourself.",
    });

    // Return to home page instead of just closing
    if (onReturnHome) {
      onReturnHome();
    }
    onClose();
  };

  if (isLoading) {
    return (
      <Dialog open={isOpen} onOpenChange={onClose}>
        <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
          <div className="flex items-center justify-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <div className="text-center">
            <DialogTitle className="text-2xl font-bold text-foreground mb-2">
              Session Summary
            </DialogTitle>
            <p className="text-muted-foreground">
              Here's what we covered in your session with {therapistName}
            </p>
          </div>
        </DialogHeader>

        <div className="space-y-6">
          {/* Session Duration */}
          <Card className="bg-blue-50 border-blue-200">
            <CardContent className="p-4">
              <div className="flex items-center space-x-3">
                <Clock className="w-5 h-5 text-blue-600" />
                <div>
                  <h4 className="font-semibold text-blue-900">Session Duration</h4>
                  <p className="text-blue-800">{formatDuration(duration)}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Key Topics */}
          {session?.keyTopics && Array.isArray(session.keyTopics) && session.keyTopics.length > 0 && (
            <Card className="bg-green-50 border-green-200">
              <CardContent className="p-4">
                <div className="flex items-start space-x-3">
                  <Target className="w-5 h-5 text-green-600 mt-1" />
                  <div className="flex-1">
                    <h4 className="font-semibold text-green-900 mb-2">Key Topics Discussed</h4>
                    <ul className="text-green-800 space-y-1">
                      {session.keyTopics.map((topic: string, index: number) => (
                        <li key={index} className="flex items-center space-x-2">
                          <span className="w-1.5 h-1.5 bg-green-600 rounded-full"></span>
                          <span>{topic}</span>
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          {/* CBT Techniques */}
          {session?.cbtTechniques && Array.isArray(session.cbtTechniques) && session.cbtTechniques.length > 0 && (
            <Card className="bg-purple-50 border-purple-200">
              <CardContent className="p-4">
                <div className="flex items-start space-x-3">
                  <Lightbulb className="w-5 h-5 text-purple-600 mt-1" />
                  <div className="flex-1">
                    <h4 className="font-semibold text-purple-900 mb-2">CBT Techniques Used</h4>
                    <ul className="text-purple-800 space-y-1">
                      {session.cbtTechniques.map((technique: string, index: number) => (
                        <li key={index} className="flex items-center space-x-2">
                          <span className="w-1.5 h-1.5 bg-purple-600 rounded-full"></span>
                          <span>{technique}</span>
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Homework & Next Steps */}
          {session?.homework && Array.isArray(session.homework) && session.homework.length > 0 && (
            <Card className="bg-orange-50 border-orange-200">
              <CardContent className="p-4">
                <div className="flex items-start space-x-3">
                  <CheckSquare className="w-5 h-5 text-orange-600 mt-1" />
                  <div className="flex-1">
                    <h4 className="font-semibold text-orange-900 mb-2">Homework & Next Steps</h4>
                    <ul className="text-orange-800 space-y-1">
                      {session.homework.map((task: string, index: number) => (
                        <li key={index} className="flex items-center space-x-2">
                          <span className="w-1.5 h-1.5 bg-orange-600 rounded-full"></span>
                          <span>{task}</span>
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Therapist Notes */}
          {session?.therapistNotes && (
            <Card className="bg-teal-50 border-teal-200">
              <CardContent className="p-4">
                <div className="flex items-start space-x-3">
                  <Heart className="w-5 h-5 text-teal-600 mt-1" />
                  <div className="flex-1">
                    <h4 className="font-semibold text-teal-900 mb-2">
                      {therapistName}'s Notes
                    </h4>
                    <p className="text-teal-800 italic">
                      "{session.therapistNotes}"
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Default content when no session data */}
          {(!session?.keyTopics || !Array.isArray(session?.keyTopics) || session?.keyTopics.length === 0) && 
           (!session?.cbtTechniques || !Array.isArray(session?.cbtTechniques) || session?.cbtTechniques.length === 0) && 
           (!session?.homework || !Array.isArray(session?.homework) || session?.homework.length === 0) && (
            <Card className="bg-muted/50">
              <CardContent className="p-6 text-center">
                <p className="text-muted-foreground">
                  Session summary is being generated. This may take a moment.
                </p>
              </CardContent>
            </Card>
          )}
        </div>

        {/* Action Buttons */}
        <div className="flex space-x-3 pt-4">
          <Button
            variant="outline"
            onClick={handleDownloadPDF}
            className="flex-1"
          >
            <Download className="w-4 h-4 mr-2" />
            Download PDF
          </Button>

          <Button
            variant="outline"
            onClick={handleEmailSummary}
            className="flex-1"
          >
            <Mail className="w-4 h-4 mr-2" />
            Email Summary
          </Button>

          {onViewAllSummaries && (
            <Button
              variant="outline"
              onClick={onViewAllSummaries}
              className="flex-1"
            >
              View All Sessions
            </Button>
          )}

          <Button
            onClick={handleFinish}
            className="flex-1 bg-primary hover:bg-primary/90"
          >
            <Check className="w-4 h-4 mr-2" />
            Done
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/sidebar.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

import { 
  MessageCircle, 
  BookOpen, 
  Smile, 
  Wrench, 
  Phone, 
  X,
  ChevronRight,
  ChevronDown,
  FileText,
  Calendar,
  Clock,
  Home,
  Settings,
  User
} from "lucide-react";
import type { CurrentPage } from "@/App";
import type { Session } from "@shared/schema";

interface SidebarProps {
  isOpen: boolean;
  onClose: () => void;
  sessionId: number | null;
  onPageChange: (page: CurrentPage) => void;
  currentPage: CurrentPage;
  onEndSession?: () => void;
}

export default function Sidebar({ isOpen, onClose, sessionId, onPageChange, currentPage, onEndSession }: SidebarProps) {
  const [activeSection, setActiveSection] = useState(currentPage);
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);
  const [pendingNavigation, setPendingNavigation] = useState<CurrentPage | null>(null);

  // Update active section when current page changes
  useEffect(() => {
    setActiveSection(currentPage);
  }, [currentPage]);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // End session mutation
  const endSessionMutation = useMutation({
    mutationFn: async () => {
      if (!sessionId) throw new Error("No active session");

      const response = await apiRequest("POST", `/api/sessions/${sessionId}/end`);
      return response.json();
    },
    onSuccess: () => {
      // Stop any ongoing speech synthesis
      if (typeof window !== 'undefined' && window.speechSynthesis) {
        window.speechSynthesis.cancel();
      }

      // Clear session from localStorage to prevent recreation
      localStorage.removeItem('eunoia-session-id');

      // Invalidate sessions cache
      queryClient.invalidateQueries({ queryKey: ["/api/sessions/summaries"] });
      queryClient.invalidateQueries({ queryKey: ["/api/sessions"] });

      // Only show toast if we're ending from a non-session page
      if (currentPage !== 'session') {
        toast({
          title: "Session Ended",
          description: "Your session has been ended.",
        });
      }

      // Navigate to the pending page
      if (pendingNavigation) {
        onPageChange(pendingNavigation);
        setPendingNavigation(null);
      } else {
        // Default to home if no pending navigation
        onPageChange('home');
      }
      onClose();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: "Failed to end session properly.",
        variant: "destructive",
      });
    }
  });

  const handleNavigation = (page: CurrentPage) => {
    // Only show confirmation if we're currently in an active session AND on the session page
    if (sessionId && currentPage === 'session') {
      setPendingNavigation(page);
      setShowConfirmDialog(true);
    } else if (sessionId && currentPage !== 'session') {
      // If there's a session but we're not on the session page, auto-end it silently
      setPendingNavigation(page);
      endSessionMutation.mutate();
    } else {
      // No active session, navigate directly
      onPageChange(page);
      onClose();
    }
  };

  const handleConfirmEndSession = () => {
    setShowConfirmDialog(false);
    // Call the onEndSession callback if available (from session page)
    if (onEndSession) {
      onEndSession();
    } else {
      endSessionMutation.mutate();
    }
  };

  const handleCancelNavigation = () => {
    setShowConfirmDialog(false);
    setPendingNavigation(null);
  };



  const menuItems = [
    {
      id: "home",
      label: "Home",
      icon: Home,
      active: activeSection === "home" && sessionId === null, // Only active when on home without session
      action: () => handleNavigation('home')
    },
    {
      id: "session",
      label: "Talk to Eunoia",
      icon: MessageCircle,
      active: activeSection === "session", // Show active when on session page
      action: () => {
        if (sessionId) {
          // Navigate to active session
          handleNavigation('session');
        } else {
          // No session, go to home to start one
          handleNavigation('home');
        }
      }
    },
    {
      id: "journal",
      label: "Journal", 
      icon: BookOpen,
      active: activeSection === "journal",
      action: () => handleNavigation('journal')
    },
    {
      id: "mood",
      label: "Mood Check-in",
      icon: Smile,
      active: activeSection === "mood",
      action: () => handleNavigation('mood')
    },
    {
      id: "cbt",
      label: "CBT Coping Tools",
      icon: Wrench,
      active: activeSection === "cbt-tools",
      action: () => handleNavigation('cbt-tools')
    },
    {
      id: "summaries",
      label: "Session Summaries",
      icon: FileText,
      active: activeSection === "session-summaries",
      action: () => handleNavigation('session-summaries')
    },
    {
      id: "settings",
      label: "Session Settings",
      icon: Settings,
      active: activeSection === "settings",
      action: () => handleNavigation('settings')
    },
    {
      id: "account",
      label: "Account Settings",
      icon: User,
      active: activeSection === "account",
      action: () => handleNavigation('account')
    }
  ];

  const cbtTools = [
    "Breathing Exercises",
    "Thought Challenging",
    "Grounding Techniques",
    "Progressive Muscle Relaxation",
    "Cognitive Restructuring",
    "Mindfulness Meditation"
  ];

  const journalPrompts = [
    "How am I feeling right now?",
    "What am I grateful for today?",
    "What challenged me today?",
    "What did I learn about myself?",
    "What would I like to focus on tomorrow?"
  ];

  const SessionSummariesSection = () => {
    const { data: sessions = [], isLoading } = useQuery<Session[]>({
      queryKey: ["/api/sessions/summaries"],
    });

    if (isLoading) {
      return (
        <div className="space-y-4">
          <div className="animate-pulse">
            <div className="h-4 bg-gray-200 rounded w-3/4 mb-4"></div>
            <div className="space-y-2">
              <div className="h-16 bg-gray-200 rounded"></div>
              <div className="h-16 bg-gray-200 rounded"></div>
            </div>
          </div>
        </div>
      );
    }

    const completedSessions = sessions.filter(session => session.summary);

    return (
      <div className="space-y-4">
        <div>
          <div className="flex items-center justify-between mb-3">
            <h4 className="font-medium">Past Session Summaries</h4>
            {completedSessions.length > 0 && (
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={() => handleNavigation('session-summaries')}
                className="text-xs"
              >
                View All
              </Button>
            )}
          </div>
          {completedSessions.length === 0 ? (
            <div className="text-center py-8">
              <FileText className="w-12 h-12 mx-auto text-muted-foreground mb-2" />
              <p className="text-sm text-muted-foreground">
                No session summaries yet. Complete a therapy session to see summaries here.
              </p>
            </div>
          ) : (
            <div className="space-y-3">
              {completedSessions.slice(0, 2).map((session) => (
                <Card key={session.id} className="cursor-pointer hover:bg-accent/5 transition-colors">
                  <CardContent className="p-4">
                    <div className="flex items-start justify-between mb-2">
                      <h5 className="font-medium text-sm">
                        Session {session.id}
                      </h5>
                      <span className="text-xs text-muted-foreground">
                        {session.duration ? `${Math.floor(session.duration / 60)}min` : ''}
                      </span>
                    </div>
                    <div className="flex items-center gap-2 text-xs text-muted-foreground mb-2">
                      <Calendar className="w-3 h-3" />
                      {session.startTime ? new Date(session.startTime).toLocaleDateString() : 'Unknown date'}
                    </div>
                    <p className="text-xs text-muted-foreground line-clamp-2">
                      {session.summary ? session.summary.substring(0, 100) + '...' : 'No summary available'}
                    </p>
                    {session.keyTopics && session.keyTopics.length > 0 && (
                      <div className="flex flex-wrap gap-1 mt-2">
                        {session.keyTopics.slice(0, 2).map((topic, index) => (
                          <span key={index} className="text-xs bg-primary/10 text-primary px-2 py-1 rounded">
                            {topic}
                          </span>
                        ))}
                        {session.keyTopics.length > 2 && (
                          <span className="text-xs text-muted-foreground">+{session.keyTopics.length - 2} more</span>
                        )}
                      </div>
                    )}
                  </CardContent>
                </Card>
              ))}
              {completedSessions.length > 2 && (
                <div className="text-center pt-2">
                  <Button 
                    variant="outline" 
                    size="sm" 
                    onClick={() => handleNavigation('session-summaries')}
                    className="text-xs"
                  >
                    View All {completedSessions.length} Sessions
                  </Button>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    );
  };

  const renderContent = () => {
    switch (activeSection) {
      case "session":
        return (
          <div className="space-y-4">
            <p className="text-sm text-muted-foreground">
              You're currently in an active therapy session. Use the main interface to communicate with your therapist.
            </p>
            <div className="bg-primary/10 rounded-lg p-3">
              <p className="text-sm font-medium text-primary">Session Active</p>
              <p className="text-xs text-muted-foreground">
                Your conversation is being recorded for session summary purposes.
              </p>
            </div>
          </div>
        );

      case "journal":
        return (
          <div className="space-y-4">
            <p className="text-sm text-muted-foreground">
              Journal features are coming soon. In the meantime, you can discuss your thoughts and feelings during a therapy session.
            </p>
          </div>
        );

      case "mood":
        return (
          <div className="space-y-4">
            <p className="text-sm text-muted-foreground">
              Mood check-in features are coming soon. In the meantime, you can discuss your feelings during a therapy session.
            </p>
          </div>
        );

      case "cbt":
        return (
          <div className="space-y-4">
            <div>
              <h4 className="font-medium mb-3">CBT Coping Tools</h4>
              <div className="space-y-2 max-h-64 overflow-y-auto scrollbar-thin scrollbar-thumb-border scrollbar-track-background">
                {cbtTools.map((tool, index) => (
                  <Button
                    key={index}
                    variant="outline"
                    className="w-full justify-between text-left h-auto py-3 px-4 text-foreground border-border hover:bg-accent/10 break-words"
                  >
                    <span className="text-sm leading-relaxed flex-1 text-left">{tool}</span>
                    <ChevronRight className="h-4 w-4 flex-shrink-0 ml-2" />
                  </Button>
                ))}
              </div>
            </div>

            <div className="bg-accent/10 rounded-lg p-3">
              <h5 className="font-medium text-accent mb-2">Quick Exercise</h5>
              <p className="text-sm text-muted-foreground mb-2">
                Try the 4-7-8 breathing technique:
              </p>
              <ol className="text-sm space-y-1 text-muted-foreground">
                <li>1. Inhale for 4 counts</li>
                <li>2. Hold for 7 counts</li>
                <li>3. Exhale for 8 counts</li>
                <li>4. Repeat 3-4 times</li>
              </ol>
            </div>
          </div>
        );

      case "summaries":
        return <SessionSummariesSection />;

      default:
        return null;
    }
  };

  return (
    <>
      {/* Overlay */}
      {isOpen && (
        <div 
          className="fixed inset-0 bg-black/50 z-40"
          onClick={onClose}
        />
      )}

      {/* Sidebar */}
      <div className={`
        fixed z-50 h-full bg-background shadow-lg border-r border-border
        w-80 transform transition-transform duration-300 ease-in-out
        ${isOpen ? 'translate-x-0' : '-translate-x-full'}
      `}>
        <div className="flex flex-col h-full max-h-screen">
          {/* Fixed Header */}
          <div className="p-4 border-b border-border flex-shrink-0">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-lg font-bold text-primary">Eunoia</h2>
                <p className="text-xs text-muted-foreground">Your AI Therapeutic Companion</p>
              </div>
              <Button
                variant="ghost"
                size="sm"
                onClick={onClose}
              >
                <X className="h-5 w-5" />
              </Button>
            </div>
          </div>

          {/* Scrollable Content Area */}
          <div className="flex-1 overflow-y-auto min-h-0">
            <div className="space-y-4 p-4">
              {/* Navigation */}
              <nav className="space-y-2">
                {menuItems.map((item) => {
                  const IconComponent = item.icon;
                  return (
                    <Button
                      key={item.id}
                      variant={item.active ? "default" : "ghost"}
                      className="w-full justify-start space-x-3 h-12"
                      onClick={item.action}
                    >
                      <IconComponent className="h-5 w-5" />
                      <span>{item.label}</span>
                    </Button>
                  );
                })}
              </nav>

              {/* Additional Content */}
              <div className="space-y-4">
                {renderContent()}
              </div>

              {/* Crisis Support */}
              <div className="mt-6">
                <Card className="bg-red-50 dark:bg-red-950 border-red-200 dark:border-red-800">
                  <CardContent className="p-3">
                    <div className="flex items-center space-x-2 mb-2">
                      <Phone className="h-4 w-4 text-red-600 dark:text-red-400" />
                      <span className="font-semibold text-red-800 dark:text-red-200 text-sm">Crisis Support</span>
                    </div>
                    <div className="space-y-1 text-xs">
                      <div className="font-medium text-red-800 dark:text-red-200">988 - Suicide & Crisis Lifeline</div>
                      <div className="font-medium text-red-800 dark:text-red-200">911 - Emergency Services</div>
                    </div>
                  </CardContent>
                </Card>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Confirmation Dialog */}
      <AlertDialog open={showConfirmDialog} onOpenChange={setShowConfirmDialog}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>End Current Session?</AlertDialogTitle>
            <AlertDialogDescription>
              You have an active therapy session. Are you sure you want to end it and navigate to a different page? 
              Your session progress will be saved and a summary will be generated.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel onClick={handleCancelNavigation}>
              Stay in Session
            </AlertDialogCancel>
            <AlertDialogAction 
              onClick={handleConfirmEndSession}
              disabled={endSessionMutation.isPending}
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
            >
              {endSessionMutation.isPending ? "Ending Session..." : "End Session"}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

client/src/components/voice-interface.ts.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { 
  Mic, 
  MicOff, 
  Volume2, 
  Pause, 
  Lightbulb, 
  User, 
  UserCheck 
} from "lucide-react";
import { useSpeech } from "@/hooks/use-speech";
import type { Message } from "@shared/schema";

interface VoiceInterfaceProps {
  therapistName: string;
  onSendMessage: (content: string, isVoice: boolean) => void;
  messages: Message[];
  isLoading: boolean;
  sessionEnded?: boolean;
}

export default function VoiceInterface({ 
  therapistName, 
  onSendMessage, 
  messages, 
  isLoading,
  sessionEnded = false 
}: VoiceInterfaceProps) {
  const [isRecording, setIsRecording] = useState(false);
  const [currentTranscript, setCurrentTranscript] = useState("");
  const [lastAIResponse, setLastAIResponse] = useState("");

  const { 
    startListening, 
    stopListening, 
    speak, 
    stopSpeaking,
    isListening, 
    isSupported 
  } = useSpeech();

  // Get latest AI response
  useEffect(() => {
    const latestAIMessage = messages
      .filter(m => m.role === 'assistant')
      .slice(-1)[0];

    if (latestAIMessage && latestAIMessage.content !== lastAIResponse) {
      setLastAIResponse(latestAIMessage.content);
      // Auto-speak the response
      speak(latestAIMessage.content);
    }
  }, [messages, lastAIResponse, speak]);

  // Stop all speech when session ends
  useEffect(() => {
    if (sessionEnded) {
      stopSpeaking();
      stopListening();
    }
  }, [sessionEnded, stopSpeaking, stopListening]);

  // Cleanup effect when component unmounts or mode changes
  useEffect(() => {
    return () => {
      stopSpeaking();
      stopListening();
    };
  }, [stopSpeaking, stopListening]);

  const handleStartRecording = async () => {
    if (!isSupported) {
      alert("Speech recognition is not supported in this browser.");
      return;
    }

    setIsRecording(true);
    setCurrentTranscript("");

    try {
      const transcript = await startListening();
      setCurrentTranscript(transcript);

      if (transcript.trim()) {
        onSendMessage(transcript, true);
      }
    } catch (error) {
      console.error('Speech recognition error:', error);
    } finally {
      setIsRecording(false);
    }
  };

  const handleStopRecording = () => {
    setIsRecording(false);
    stopListening();

    if (currentTranscript.trim()) {
      onSendMessage(currentTranscript, true);
    }
  };

  const handleRepeatResponse = () => {
    if (lastAIResponse) {
      speak(lastAIResponse);
    }
  };

  const toggleRecording = () => {
    if (isRecording) {
      handleStopRecording();
    } else {
      handleStartRecording();
    }
  };

  const latestUserMessage = messages.filter(m => m.role === 'user').slice(-1)[0];
  const latestAIMessage = messages.filter(m => m.role === 'assistant').slice(-1)[0];

  return (
    <div className="h-full flex flex-col items-center justify-center p-8 space-y-8">
      <div className="max-w-2xl mx-auto text-center">

        {/* Therapist Avatar */}
        <div className="mb-8">
          <div className="w-32 h-32 bg-gradient-primary rounded-full mx-auto mb-4 flex items-center justify-center shadow-lg">
            <UserCheck className="text-white text-4xl" />
          </div>
          <h2 className="text-2xl font-bold text-foreground mb-2">
            {therapistName} is listening
          </h2>
          <p className="text-muted-foreground">
            {isRecording ? "I'm listening..." : "Tap the button below to start talking"}
          </p>
        </div>

        {/* Voice Recording Button */}
        <div className="mb-8">
          <Button
            onClick={toggleRecording}
            disabled={isLoading || !isSupported}
            className={`
              w-24 h-24 rounded-full shadow-lg transition-all transform hover:scale-105 
              focus:outline-none focus:ring-4 focus:ring-primary/30
              ${isRecording 
                ? 'bg-red-500 hover:bg-red-600 animate-pulse' 
                : 'bg-primary hover:bg-primary/90'
              }
            `}
          >
            {isRecording ? (
              <MicOff className="text-white text-2xl" />
            ) : (
              <Mic className="text-white text-2xl" />
            )}
          </Button>
          <p className="text-sm text-muted-foreground mt-2">
            {isRecording ? "Recording... tap to stop" : "Tap to speak"}
          </p>
        </div>

        {/* Recording Status */}
        {isRecording && (
          <Card className="mb-6 bg-blue-50 border-blue-200">
            <CardContent className="p-4">
              <div className="flex items-center justify-center space-x-2 mb-2">
                <div className="w-3 h-3 bg-red-500 rounded-full animate-pulse"></div>
                <span className="text-sm font-medium text-blue-800">Recording...</span>
              </div>
              <p className="text-sm text-blue-700">
                {currentTranscript || "Listening for your voice..."}
              </p>
            </CardContent>
          </Card>
        )}

        {/* Latest User Message */}
        {latestUserMessage && !isRecording && (
          <Card className="mb-6 bg-primary/10 border-primary/20">
            <CardContent className="p-4">
              <div className="flex items-start space-x-3">
                <User className="w-6 h-6 text-primary mt-1" />
                <div className="flex-1 text-left">
                  <p className="text-sm font-medium text-primary mb-1">You said:</p>
                  <p className="text-sm text-foreground">{latestUserMessage.content}</p>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* AI Response */}
        {latestAIMessage && !isRecording && (
          <Card className="mb-6 bg-accent/10 border-accent/20">
            <CardContent className="p-4">
              <div className="flex items-start space-x-3">
                <UserCheck className="w-6 h-6 text-accent mt-1" />
                <div className="flex-1 text-left">
                  <p className="text-sm font-medium text-accent mb-1">{therapistName}</p>
                  <p className="text-sm text-foreground">{latestAIMessage.content}</p>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Loading State */}
        {isLoading && (
          <Card className="mb-6 bg-muted/50">
            <CardContent className="p-4">
              <div className="flex items-center justify-center space-x-2">
                <div className="w-2 h-2 bg-primary rounded-full animate-pulse"></div>
                <div className="w-2 h-2 bg-primary rounded-full animate-pulse" style={{animationDelay: '0.2s'}}></div>
                <div className="w-2 h-2 bg-primary rounded-full animate-pulse" style={{animationDelay: '0.4s'}}></div>
                <span className="text-sm text-muted-foreground ml-2">
                  {therapistName} is thinking...
                </span>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Quick Actions */}
        <div className="flex flex-wrap justify-center gap-3">
          <Button
            variant="outline"
            size="sm"
            onClick={() => setIsRecording(!isRecording)}
            disabled={isLoading}
          >
            <Pause className="w-4 h-4 mr-2" />
            {isRecording ? "Pause" : "Resume"}
          </Button>

          <Button
            variant="outline"
            size="sm"
            onClick={handleRepeatResponse}
            disabled={!lastAIResponse}
          >
            <Volume2 className="w-4 h-4 mr-2" />
            Repeat Response
          </Button>

          <Button
            variant="outline"
            size="sm"
            className="bg-accent/10 text-accent hover:bg-accent/20"
          >
            <Lightbulb className="w-4 h-4 mr-2" />
            Get Coping Tool
          </Button>
        </div>

        {/* Browser Support Warning */}
        {!isSupported && (
          <Card className="mt-6 bg-warning/10 border-warning/20">
            <CardContent className="p-4">
              <p className="text-sm text-warning">
                Voice recognition is not supported in your browser. Please use a modern browser like Chrome, Firefox, or Safari for the best experience.
              </p>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

