import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { 
  Mic, 
  MicOff, 
  Volume2, 
  Pause, 
  Lightbulb, 
  User, 
  UserCheck 
} from "lucide-react";
import { useSpeech } from "@/hooks/use-speech";
import type { Message } from "@shared/schema";

interface VoiceInterfaceProps {
  therapistName: string;
  onSendMessage: (content: string, isVoice: boolean) => void;
  messages: Message[];
  isLoading: boolean;
  sessionEnded?: boolean;
}

export default function VoiceInterface({ 
  therapistName, 
  onSendMessage, 
  messages, 
  isLoading,
  sessionEnded = false 
}: VoiceInterfaceProps) {
  const [isRecording, setIsRecording] = useState(false);
  const [currentTranscript, setCurrentTranscript] = useState("");
  const [lastAIResponse, setLastAIResponse] = useState("");

  const { 
    startListening, 
    stopListening, 
    speak, 
    stopSpeaking,
    isListening, 
    isSupported 
  } = useSpeech();

  // Get latest AI response
  useEffect(() => {
    const latestAIMessage = messages
      .filter(m => m.role === 'assistant')
      .slice(-1)[0];

    if (latestAIMessage && latestAIMessage.content !== lastAIResponse) {
      setLastAIResponse(latestAIMessage.content);
      // Auto-speak the response
      speak(latestAIMessage.content);
    }
  }, [messages, lastAIResponse, speak]);

  // Stop all speech when session ends
  useEffect(() => {
    if (sessionEnded) {
      stopSpeaking();
      stopListening();
    }
  }, [sessionEnded, stopSpeaking, stopListening]);

  // Cleanup effect when component unmounts or mode changes
  useEffect(() => {
    return () => {
      stopSpeaking();
      stopListening();
    };
  }, [stopSpeaking, stopListening]);

  const handleStartRecording = async () => {
    if (!isSupported) {
      alert("Speech recognition is not supported in this browser.");
      return;
    }

    setIsRecording(true);
    setCurrentTranscript("");

    try {
      const transcript = await startListening();
      setCurrentTranscript(transcript);

      if (transcript.trim()) {
        onSendMessage(transcript, true);
      }
    } catch (error) {
      console.error('Speech recognition error:', error);
    } finally {
      setIsRecording(false);
    }
  };

  const handleStopRecording = () => {
    setIsRecording(false);
    stopListening();

    if (currentTranscript.trim()) {
      onSendMessage(currentTranscript, true);
    }
  };

  const handleRepeatResponse = () => {
