import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Menu, Settings, Heart } from "lucide-react";
import { Button } from "@/components/ui/button";
import Sidebar from "@/components/sidebar";
import type { SessionSettings, CurrentPage } from "@/App";

interface HomeProps {
  sessionSettings: SessionSettings;
  onPageChange: (page: CurrentPage) => void;
  onSessionStart: (sessionId: number) => void;
}

export default function Home({ sessionSettings, onPageChange, onSessionStart }: HomeProps) {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const { toast } = useToast();

  // Create session mutation
  const createSessionMutation = useMutation({
    mutationFn: async (settings: SessionSettings) => {
      const response = await apiRequest("POST", "/api/sessions", {
        therapistPersonality: settings.therapistPersonality,
        selectedVoice: settings.selectedVoice,
        selectedGoals: settings.selectedGoals,
      });
      return response.json();
    },
    onSuccess: (session) => {
      toast({
        title: "Session Started",
        description: "Your therapy session has begun successfully.",
      });
      onSessionStart(session.id);
    },
    onError: (error: Error) => {
      toast({
        title: "Error starting session",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const initializeSession = () => {
    createSessionMutation.mutate(sessionSettings);
  };

  const getTherapistName = () => {
    const personalities: Record<string, string> = {
      empathetic: "Dr. Emma",
      analytical: "Dr. Alex", 
      supportive: "Dr. Sam",
      mindful: "Dr. Maya"
    };
    return personalities[sessionSettings.therapistPersonality] || "Dr. Emma";
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50 relative overflow-hidden">
      {/* Sidebar */}
      <Sidebar 
        isOpen={isSidebarOpen}
        onClose={() => setIsSidebarOpen(false)}
        sessionId={null}
        onPageChange={onPageChange}
        currentPage="home"
      />

      {/* Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-gradient-to-br from-blue-400/20 to-purple-400/20 rounded-full blur-3xl"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-gradient-to-tr from-pink-400/20 to-blue-400/20 rounded-full blur-3xl"></div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 flex flex-col min-h-screen">
        {/* Header */}
        <div className="bg-white/80 backdrop-blur-sm shadow-sm border-b border-gray-200/50 p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setIsSidebarOpen(true)}
              >
                <Menu className="h-5 w-5" />
              </Button>

              <h1 className="text-xl font-bold text-primary">Eunoia</h1>
            </div>

            <Button
              variant="outline"
              size="sm"
              onClick={() => onPageChange('settings')}
              className="flex items-center space-x-2"
            >
              <Settings className="h-4 w-4" />
              <span>Customize Session</span>
            </Button>
          </div>
        </div>

        {/* Main Content Area */}
        <div className="flex-1 flex items-center justify-center p-6">
          <div className="max-w-2xl w-full">
            <div className="text-center mb-12">
              {/* Animated Heart Icon */}
              <div className="relative mb-8">
                <div className="w-16 h-16 mx-auto bg-gradient-to-br from-red-400 to-pink-500 rounded-full flex items-center justify-center shadow-lg animate-pulse">
                  <Heart className="w-8 h-8 text-white fill-current" />
                </div>
                <div className="absolute inset-0 w-16 h-16 mx-auto bg-gradient-to-br from-red-400/30 to-pink-500/30 rounded-full animate-ping"></div>
              </div>

              <h2 className="text-4xl font-bold text-gray-800 mb-4">
                Welcome to Your Safe Space
              </h2>
              <p className="text-lg text-gray-600 mb-2">
                Ready to start your session with
              </p>
              <p className="text-xl font-semibold text-gray-800 mb-6">
                {getTherapistName()}
              </p>

              {/* Session Info */}
              <div className="bg-white/80 backdrop-blur-sm rounded-lg p-6 mb-8 shadow-sm border border-gray-100">
                <div className="grid grid-cols-1 gap-4 text-sm">
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Therapist Personality:</span>
                    <span className="font-medium text-gray-800 capitalize">
                      {sessionSettings.therapistPersonality}
                    </span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Selected Goals:</span>
                    <span className="font-medium text-gray-800">
                      {sessionSettings.selectedGoals.length} goal{sessionSettings.selectedGoals.length !== 1 ? 's' : ''}
                    </span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">Voice Mode:</span>
                    <span className="font-medium text-gray-800">
                      {sessionSettings.selectedVoice}
                    </span>
                  </div>
                </div>
              </div>

              {/* Start Button */}
              <Button 
                onClick={initializeSession}
                disabled={createSessionMutation.isPending}
                size="lg"
                className="w-full h-12 text-lg font-semibold bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 transition-all duration-200 shadow-lg"
              >
                {createSessionMutation.isPending ? (
                  <div className="flex items-center space-x-2">
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                    <span>Starting Session...</span>
                  </div>
                ) : (
                  "Begin Your Session"
                )}
              </Button>

              <p className="text-sm text-gray-500 mt-4">
                Take a moment to get comfortable and prepare for your session
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

New-Eunoia/client/src/pages/journal.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { ArrowLeft, BookOpen, Plus, Calendar, Menu } from "lucide-react";
import type { JournalEntry } from "@shared/schema";
import Sidebar from "@/components/sidebar";
import type { CurrentPage } from "@/App";

interface JournalPageProps {
  sessionId?: number;
  onBack: () => void;
  onPageChange: (page: CurrentPage) => void;
  currentPage: CurrentPage;
}

const journalPrompts = [
  "How am I feeling right now?",
  "What am I grateful for today?",
  "What challenged me today?",
  "What did I learn about myself?",
  "What would I like to focus on tomorrow?",
  "What emotions came up for me today?",
  "What is one thing I did well today?",
  "What is something I want to improve?",
  "What brought me joy today?",
  "What am I worried about?"
];

export default function JournalPage({ sessionId, onBack, onPageChange, currentPage }: JournalPageProps) {
  const [newEntryTitle, setNewEntryTitle] = useState("");
  const [newEntryContent, setNewEntryContent] = useState("");
  const [selectedPrompt, setSelectedPrompt] = useState("");
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get journal entries
  const { data: entries = [], isLoading } = useQuery({
    queryKey: ["/api/journal", sessionId],
    queryFn: async () => {
      const url = sessionId ? `/api/journal?sessionId=${sessionId}` : "/api/journal";
      const response = await apiRequest("GET", url);
      return response.json();
    },
  });

  // Create journal entry mutation
  const createEntryMutation = useMutation({
    mutationFn: async (entryData: { title?: string; content: string; prompt?: string }) => {
      const response = await apiRequest("POST", "/api/journal", {
        sessionId: sessionId || null,
        title: entryData.title || null,
        content: entryData.content,
        prompt: entryData.prompt || null,
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/journal", sessionId] });
      setNewEntryTitle("");
      setNewEntryContent("");
      setSelectedPrompt("");
      toast({
        title: "Entry Saved",
        description: "Your journal entry has been saved successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to save journal entry. Please try again.",
        variant: "destructive",
      });
    }
  });

  const handlePromptSelect = (prompt: string) => {
    setSelectedPrompt(prompt);
    setNewEntryTitle(prompt);
    setNewEntryContent(""); // Clear content so user can respond to the prompt
  };

  const handleSaveEntry = () => {
    if (!newEntryContent.trim()) {
      toast({
        title: "Content Required",
        description: "Please write something before saving your entry.",
        variant: "destructive",
      });
      return;
    }

    createEntryMutation.mutate({
      title: newEntryTitle || undefined,
      content: newEntryContent,
      prompt: selectedPrompt || undefined,
    });
  };

  const formatDate = (timestamp: Date | string) => {
    const date = new Date(timestamp);
    return date.toLocaleDateString() + " at " + date.toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className="min-h-screen bg-gradient-neutral">
      <div className="flex h-screen">
        {/* Sidebar */}
        <Sidebar 
          isOpen={isSidebarOpen}
          onClose={() => setIsSidebarOpen(false)}
          sessionId={sessionId || null}
          onPageChange={onPageChange}
          currentPage={currentPage}
        />

        {/* Main Content */}
        <div className="flex-1 p-6">
          <div className="max-w-4xl mx-auto">
            {/* Header */}
            <div className="flex items-center mb-8">
              <Button
                variant="ghost"
                onClick={() => setIsSidebarOpen(true)}
                className="mr-4"
              >
                <Menu className="w-4 h-4" />
              </Button>
              <Button
                variant="ghost"
                onClick={onBack}
                className="mr-4"
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back
              </Button>
              <div className="flex items-center">
                <BookOpen className="w-6 h-6 text-primary mr-3" />
                <h1 className="text-3xl font-bold text-foreground">Journal</h1>
              </div>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              {/* New Entry Form */}
              <div className="lg:col-span-2">
                <Card className="mb-6">
                  <CardHeader>
                    <CardTitle className="flex items-center">
                      <Plus className="w-5 h-5 mr-2" />
                      New Journal Entry
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div>
                      <label className="text-sm font-medium mb-2 block">Title (Optional)</label>
                      <Input
                        value={newEntryTitle}
                        onChange={(e) => setNewEntryTitle(e.target.value)}
                        placeholder="Give your entry a title..."
                      />
                    </div>

                    <div>
                      <label className="text-sm font-medium mb-2 block">Your Thoughts</label>
                      <Textarea
                        value={newEntryContent}
                        onChange={(e) => setNewEntryContent(e.target.value)}
                        placeholder="Write about your thoughts, feelings, experiences..."
                        className="min-h-32"
                        rows={6}
                      />
                    </div>

                    <Button
                      onClick={handleSaveEntry}
                      disabled={createEntryMutation.isPending || !newEntryContent.trim()}
                      className="w-full"
                    >
                      {createEntryMutation.isPending ? "Saving..." : "Save Entry"}
                    </Button>
                  </CardContent>
                </Card>

                {/* Previous Entries */}
                <div className="space-y-4">
                  <h2 className="text-xl font-semibold text-foreground mb-4">Previous Entries</h2>

                  {isLoading ? (
                    <div className="text-center py-8">
                      <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
                    </div>
                  ) : entries.length === 0 ? (
                    <Card className="text-center py-8">
                      <CardContent>
                        <BookOpen className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                        <p className="text-muted-foreground">No journal entries yet. Start writing your first entry above!</p>
                      </CardContent>
                    </Card>
                  ) : (
                    entries.map((entry: JournalEntry) => (
                      <Card key={entry.id} className="hover:shadow-md transition-shadow">
                        <CardContent className="p-6">
                          <div className="flex items-start justify-between mb-3">
                            <div className="flex-1">
                              {entry.title && (
                                <h3 className="font-semibold text-foreground mb-2">{entry.title}</h3>
                              )}
                              {entry.prompt && entry.prompt !== entry.title && (
                                <p className="text-sm text-primary mb-2 italic">Prompt: {entry.prompt}</p>
                              )}
                            </div>
                            <div className="flex items-center text-sm text-muted-foreground">
                              <Calendar className="w-4 h-4 mr-1" />
                              {formatDate(entry.timestamp || new Date())}
                            </div>
                          </div>
                          <p className="text-foreground whitespace-pre-wrap">{entry.content}</p>
                        </CardContent>
                      </Card>
                    ))
                  )}
                </div>
              </div>

              {/* Prompts Sidebar */}
              <div>
                <Card className="sticky top-6">
                  <CardHeader>
                    <CardTitle className="text-lg">Writing Prompts</CardTitle>
                    <p className="text-sm text-muted-foreground">
                      Use these prompts to get started with your journaling
                    </p>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-2">
                      {journalPrompts.map((prompt, index) => (
                        <Button
                          key={index}
                          variant="outline"
                          size="sm"
                          className="w-full justify-start text-left h-auto py-3 px-3"
                          onClick={() => handlePromptSelect(prompt)}
                        >
                          <span className="text-sm">{prompt}</span>
                        </Button>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

