                    </div>
                  )}

                  {/* Therapist Notes */}
                  {session.therapistNotes && (
                    <div>
                      <h4 className="font-semibold mb-2 flex items-center gap-2">
                        <FileText className="w-4 h-4 text-blue-600" />
                        Therapist Notes
                      </h4>
                      <p className="text-muted-foreground bg-blue-50 p-4 rounded-lg border border-blue-200">
                        {session.therapistNotes}
